function maxProfit(prices: Array<number>): number {
  let n: number = prices.length;
  let profit: number = 0;
  let i: number = 1;
  while (i < n) {
    let diff: number = prices[i] - prices[i - 1];
    if (diff > 0) {
      profit = profit + diff;
    }
    i = i + 1;
  }
  return profit;
}

