function canCompleteCircuit(gas: Array<number>, cost: Array<number>) : number {
	let n: number = gas.length
	let total: number = 0
	let tank: number = 0
	let start: number = 0
	let i: number = 0
	while ((i < n)) {
		total = ((total + gas[i]) - cost[i])
		tank = ((tank + gas[i]) - cost[i])
		if ((tank < 0)) {
			start = (i + 1)
			tank = 0
		}
		i = (i + 1)
	}
	if ((total >= 0)) {
		return start
	}
	return (-1)
}

