function findPeakElement(nums: Array<number>) : number {
	let n: number = nums.length
	let left: number = 0
	let right: number = (n - 1)
	while ((left < right)) {
		let mid: number = Math.trunc(((left + right)) / 2)
		if ((nums[mid] > nums[(mid + 1)])) {
			right = mid
		} else {
			left = (mid + 1)
		}
	}
	return left
}

