function convertToTitle(columnNumber: number) : string {
	let letters: string = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
	let n: number = columnNumber
	let result: string = ""
	while ((n > 0)) {
		n = (n - 1)
		let idx: number = (n % 26)
		result = letters.slice(idx, (idx + 1)) + result
		n = Math.trunc(n / 26)
	}
	return result
}

