function rob(nums: Array<number>) : number {
	let n: number = nums.length
	if ((n == 0)) {
		return 0
	}
	if ((n == 1)) {
		return nums[0]
	}
	let prev2: number = nums[0]
	let prev1: number = nums[0]
	if ((nums[1] > prev1)) {
		prev1 = nums[1]
	}
	let i: number = 2
	while ((i < n)) {
		let take: number = (prev2 + nums[i])
		let best: number = prev1
		if ((take > best)) {
			best = take
		}
		prev2 = prev1
		prev1 = best
		i = (i + 1)
	}
	return prev1
}

