String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

List<List<int>> create_all_state(int increment, int total, int level, List<int> current, List<List<int>> result) {
  if (level == 0) {
    return ([...result, current] as List).map((e) => (e as List).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList()).toList();
  }
  int i = increment;
  while (i <= total - level + 1) {
    List<int> next_current = [...current, i];
    result = create_all_state(i + 1, total, level - 1, next_current, result);
    i = i + 1;
  }
  return result;
}

List<List<int>> generate_all_combinations(int n, int k) {
  if (k < 0 || n < 0) {
    return ([] as List).map((e) => (e as List).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList()).toList();
  }
  List<List<int>> result = [];
  return create_all_state(1, n, k, <int>[], result);
}

