// Generated by Mochi transpiler
dynamic _substr(dynamic s, num start, num end) {
  int n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  if (s is String) {
    return s.substring(s0, e0);
  }
  return s.sublist(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { var i = v.toInt(); if (i == 0) return '0'; return i.toString(); } return v.toString(); }

List<List<int>> backtrack(List<int> candidates, int start, int target, List<int> path, List<List<int>> result) {
  if (target == 0) {
    return ([...result, path] as List).map((e) => ((e as List).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList() as List<int>)).toList();
  }
  int i = start;
  while (i < candidates.length) {
    int value = candidates[i];
    if (value <= target) {
    List<int> new_path = [...path, value];
    result = backtrack(candidates, i, target - value, new_path, result);
  }
    i = i + 1;
  }
  return result;
}

List<List<int>> combination_sum(List<int> candidates, int target) {
  List<int> path = [];
  List<List<int>> result = [];
  return backtrack(candidates, 0, target, path, result);
}

