// Generated by Mochi transpiler
dynamic _substr(dynamic s, num start, num end) {
  int n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  if (s is String) {
    return s.substring(s0, e0);
  }
  return s.sublist(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { var i = v.toInt(); if (i == 0) return '0'; return i.toString(); } return v.toString(); }


Never _error(String msg) {
  throw Exception(msg);
}

String int_to_binary(int n) {
  if (n == 0) {
    return "0";
  }
  String res = "";
  int _num = n;
  while (_num > 0) {
    res = _str(_num % 2) + res;
    _num = _num ~/ 2;
  }
  return res;
}

String pad_left(String s, int width) {
  String res = s;
  while (res.length < width) {
    res = "0" + res;
  }
  return res;
}

String binary_xor(int a, int b) {
  if (a < 0 || b < 0) {
    _error("the value of both inputs must be positive");
  }
  String a_bin = int_to_binary(a);
  String b_bin = int_to_binary(b);
  int max_len = (a_bin.length > b_bin.length ? a_bin.length : b_bin.length);
  String a_pad = pad_left(a_bin, max_len);
  String b_pad = pad_left(b_bin, max_len);
  int i = 0;
  String result = "";
  while (i < max_len) {
    if (a_pad.substring(i, i + 1) != b_pad.substring(i, i + 1)) {
    result = result + "1";
  } else {
    result = result + "0";
  }
    i = i + 1;
  }
  return "0b" + result;
}

void main() {
  print(binary_xor(25, 32));
  print(binary_xor(37, 50));
  print(binary_xor(21, 30));
  print(binary_xor(58, 73));
  print(binary_xor(0, 255));
  print(binary_xor(256, 256));
}
