// Generated by Mochi transpiler
dynamic _substr(dynamic s, num start, num end) {
  int n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  if (s is String) {
    return s.substring(s0, e0);
  }
  return s.sublist(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { var i = v.toInt(); if (i == 0) return '0'; return i.toString(); } return v.toString(); }

String row_string(List<int> row) {
  String s = "[";
  int i = 0;
  while (i < row.length) {
    s = s + _str(row[i]);
    if (i < row.length - 1) {
    s = s + ", ";
  }
    i = i + 1;
  }
  s = s + "]";
  return s;
}

void print_kmap(List<List<int>> kmap) {
  int i = 0;
  while (i < kmap.length) {
    print(row_string(kmap[i]));
    i = i + 1;
  }
}

String join_terms(List<String> terms) {
  if (terms.length == 0) {
    return "";
  }
  String res = terms[0];
  int i = 1;
  while (i < terms.length) {
    res = res + " + " + terms[i];
    i = i + 1;
  }
  return res;
}

String simplify_kmap(List<List<int>> board) {
  List<String> terms = <String>[];
  int a = 0;
  while (a < board.length) {
    List<int> row = board[a];
    int b = 0;
    while (b < row.length) {
    int item = row[b];
    if (item != 0) {
    String term = (a != 0 ? "A" : "A'") + (b != 0 ? "B" : "B'");
    terms = [...terms, term];
  }
    b = b + 1;
  }
    a = a + 1;
  }
  String expr = join_terms(terms);
  return expr;
}

List<List<int>> kmap = [[0, 1], [1, 1]];
void main() {
  print_kmap(kmap);
  print("Simplified Expression:");
  print(simplify_kmap(kmap));
}
