  while (i < s.length) {
    String c = s.substring(i, i + 1);
    if (c != "0" && c != "1") {
    throw Exception("Non-binary value was passed to the function");
  }
    i = i + 1;
  }
  int groups = s.length ~/ 4 + 1;
  int pad_len = groups * 4 - s.length;
  s = repeat_char("0", pad_len) + s;
