int parse_decimal(String s) {
  if (s.length == 0) {
    throw Exception("Invalid IPv4 address format");
  }
  int value = 0;
  int i = 0;
  while (i < s.length) {
    String c = s.substring(i, i + 1);
    if (c.compareTo("0") < 0 || c.compareTo("9") > 0) {
    throw Exception("Invalid IPv4 address format");
  }
    value = value * 10 + ((c).codeUnitAt(0));
    i = i + 1;
  }
  return value;
}

