int round_to_int(double x) {
  if (x >= 0.0) {
    return x + 0.5 as int;
  }
  return x - 0.5 as int;
}

int molarity_to_normality(double nfactor, double moles, double volume) {
  return round_to_int(moles / volume * nfactor);
}

int moles_to_pressure(double volume, double moles, double temperature) {
  return round_to_int(moles * 0.0821 * temperature / volume);
}

int moles_to_volume(double pressure, double moles, double temperature) {
  return round_to_int(moles * 0.0821 * temperature / pressure);
}

int pressure_and_volume_to_temperature(double pressure, double moles, double volume) {
  return round_to_int(pressure * volume / (0.0821 * moles));
}

