double PI = 3.141592653589793;
double sqrtApprox(double x) {
  double guess = x / 2.0;
  int i = 0;
  while (i < 20) {
    guess = (guess + x / guess) / 2.0;
    i = i + 1;
  }
  return guess;
}

double atanApprox(double x) {
  if (x > 1.0) {
    return PI / 2.0 - x / (x * x + 0.28);
  }
  if (x < -1.0) {
    return -PI / 2.0 - x / (x * x + 0.28);
  }
  return x / (1.0 + 0.28 * x * x);
}

double atan2Approx(double y, double x) {
  if (x > 0.0) {
    double r = atanApprox(y / x);
    return r;
  }
  if (x < 0.0) {
    if (y >= 0.0) {
    return atanApprox(y / x) + PI;
  };
    return atanApprox(y / x) - PI;
  }
  if (y > 0.0) {
    return PI / 2.0;
  }
  if (y < 0.0) {
    return -PI / 2.0;
  }
  return 0.0;
}

double deg(double rad) {
  return rad * 180.0 / PI;
}

double floor(double x) {
  int i = x as int;
  if ((i as double) > x) {
    i = i - 1;
  }
  return i as double;
}

double pow10(int n) {
  double p = 1.0;
  int i = 0;
  while (i < n) {
    p = p * 10.0;
    i = i + 1;
  }
  return p;
}

double round(double x, int n) {
  double m = pow10(n);
  return floor(x * m + 0.5) / m;
}

List<double> rectangular_to_polar(double real, double img) {
  double mod = round(sqrtApprox(real * real + img * img), 2);
  double ang = round(deg(atan2Approx(img, real)), 2);
  return [mod, ang];
}

void show(double real, double img) {
  List<double> r = rectangular_to_polar(real, img);
  print((r).toString());
}

void main() {
  show(5.0, -5.0);
  show(-1.0, 1.0);
  show(-1.0, -1.0);
  show(0.0000000001, 0.0000000001);
  show(-0.0000000001, 0.0000000001);
  show(9.75, 5.93);
  show(10000.0, 99999.0);
}
