String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

int equilibrium_index(List<int> arr) {
  int total = 0;
  int i = 0;
  while (i < arr.length) {
    total = total + arr[i];
    i = i + 1;
  }
  int left = 0;
  i = 0;
  while (i < arr.length) {
    total = total - arr[i];
    if (left == total) {
    return i;
  }
    left = left + arr[i];
    i = i + 1;
  }
  return -1;
}

