bool is_monotonic(List<int> nums) {
  if (nums.length <= 2) {
    return true;
  }
  bool increasing = true;
  bool decreasing = true;
  int i = 0;
  while (i < nums.length - 1) {
    if (nums[i] > nums[i + 1]) {
    increasing = false;
  }
    if (nums[i] < nums[i + 1]) {
    decreasing = false;
  }
    i = i + 1;
  }
  return increasing || decreasing;
}

