List<int> insertion_sort(List<int> a) {
  int i = 1;
  while (i < a.length) {
    int key = a[i];
    int j = i - 1;
    while (j >= 0 && a[j] > key) {
    while (a.length <= j + 1) { a.add(0); } a[j + 1] = a[j];
    j = j - 1;
  }
    while (a.length <= j + 1) { a.add(0); } a[j + 1] = key;
    i = i + 1;
  }
  return a;
}

List<List<int>> median_filter(List<List<int>> gray_img, int mask) {
  int rows = gray_img.length;
  int cols = gray_img[0].length;
  int bd = mask ~/ 2;
  List<List<int>> result = <List<int>>[];
  int i = 0;
  while (i < rows) {
    List<int> row = <int>[];
    int j = 0;
    while (j < cols) {
    row = (row..add(0));
    j = j + 1;
  }
    result = ((result..add(row)) as List).map((e) => ((e as List).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList() as List<int>)).toList();
    i = i + 1;
  }
  i = bd;
  while (i < rows - bd) {
    int j = bd;
    while (j < cols - bd) {
    List<int> kernel = <int>[];
    int x = i - bd;
    while (x <= i + bd) {
    int y = j - bd;
    while (y <= j + bd) {
    kernel = (kernel..add(gray_img[x][y]));
    y = y + 1;
  }
    x = x + 1;
  }
    kernel = insertion_sort(kernel);
    int idx = mask * mask ~/ 2;
    while (result[i]!.length <= j) { result[i]!.add(0); } result[i]![j] = kernel[idx];
    j = j + 1;
  }
    i = i + 1;
  }
  return result;
}

