List<int> document_frequency(String term, String corpus) {
  String clean = clean_text(corpus, true);
  dynamic docs = clean.split("\n");
  String t = to_lowercase(term);
  int matches = 0;
  int i = 0;
  while (i < docs.length) {
    if (docs[i].contains(t)) {
    matches = matches + 1;
  }
    i = i + 1;
  }
  return [matches, docs.length];
}

