String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

double abs_float(double x) {
  if (x < 0.0) {
    return -x;
  } else {
    return x;
  }
}

double trapezoidal_area(dynamic f, double x_start, double x_end, int steps) {
  double step = (x_end - x_start) / (steps as double);
  double x1 = x_start;
  double fx1 = f(x_start);
  double area = 0.0;
  int i = 0;
  while (i < steps) {
    double x2 = x1 + step;
    double fx2 = f(x2);
    area = area + abs_float(fx2 + fx1) * step / 2.0;
    x1 = x2;
    fx1 = fx2;
    i = i + 1;
  }
  return area;
}

double f(double x) {
  return x * x * x + x * x;
}

int i = 10;
void main() {
  print("f(x) = x^3 + x^2");
  print("The area between the curve, x = -5, x = 5 and the x axis is:");
  while (i <= 100000) {
    double result = trapezoidal_area(f, -5.0, 5.0, i);
    print("with " + (i).toString() + " steps: " + (result).toString());
    i = i * 10;
  }
}
