double mean(List<double> nums) {
  if (nums.length == 0) {
    throw Exception("List is empty");
  }
  double total = 0.0;
  int i = 0;
  while (i < nums.length) {
    total = total + nums[i];
    i = i + 1;
  }
  return total / (nums.length as double);
}

