bool check_polygon(List<double> nums) {
  if (nums.length < 2) {
    error("Monogons and Digons are not polygons in the Euclidean space");
  }
  int i = 0;
  while (i < nums.length) {
    if (nums[i] <= 0.0) {
    error("All values must be greater than 0");
