int combinations(int n, int k) {
  if (k < 0 || n < k) {
    throw Exception("Please enter positive integers for n and k where n >= k");
  }
  int res = 1;
  int i = 0;
  while (i < k) {
    res = res * (n - i);
    res = res ~/ (i + 1);
    i = i + 1;
  }
  return res;
}

