int double_factorial_recursive(int n) {
  if (n < 0) {
    throw Exception("double_factorial_recursive() not defined for negative values");
  }
  if (n <= 1) {
    return 1;
  }
  return n * double_factorial_recursive(n - 2);
}

int double_factorial_iterative(int n) {
  if (n < 0) {
    throw Exception("double_factorial_iterative() not defined for negative values");
  }
  int result = 1;
  int i = n;
  while (i > 0) {
    result = result * i;
    i = i - 2;
  }
  return result;
}

