double find_min_iterative(List<double> nums) {
  if (nums.length == 0) {
    throw Exception("find_min_iterative() arg is an empty sequence");
  }
  double min_num = nums[0];
  int i = 0;
  while (i < nums.length) {
    double _num = nums[i];
    if (_num < min_num) {
    min_num = _num;
  }
    i = i + 1;
  }
  return min_num;
}

double find_min_recursive(List<double> nums, int left, int right) {
  int n = nums.length;
  if (n == 0) {
    throw Exception("find_min_recursive() arg is an empty sequence");
  }
  if (left >= n || left < 0 - n || right >= n || right < 0 - n) {
    throw Exception("list index out of range");
  }
  int l = left;
  int r = right;
  if (l < 0) {
    l = n + l;
  }
  if (r < 0) {
    r = n + r;
  }
  if (l == r) {
    return nums[l];
  }
  int mid = (l + r) ~/ 2;
  double left_min = find_min_recursive(nums, l, mid);
  double right_min = find_min_recursive(nums, mid + 1, r);
  if (left_min <= right_min) {
    return left_min;
  }
  return right_min;
}

