double abs_val(double x) {
  if (x < 0.0) {
    return -x;
  }
  return x;
}

double pow_float(double base, int exp) {
  double result = 1.0;
  int i = 0;
  while (i < exp) {
    result = result * base;
    i = i + 1;
  }
  return result;
}

double nth_root(double value, int n) {
  if (value == 0.0) {
    return 0.0;
  }
  double x = value / (n as double);
  int i = 0;
  while (i < 20) {
    double _num = (n - 1 as double) * x + value / pow_float(x, n - 1);
    x = _num / (n as double);
    i = i + 1;
  }
  return x;
}

double minkowski_distance(List<double> point_a, List<double> point_b, int order) {
  if (order < 1) {
    throw Exception("The order must be greater than or equal to 1.");
  }
  if (point_a.length != point_b.length) {
    throw Exception("Both points must have the same dimension.");
  }
  double total = 0.0;
  int idx = 0;
  while (idx < point_a.length) {
    double diff = abs_val(point_a[idx] - point_b[idx]);
    total = total + pow_float(diff, order);
    idx = idx + 1;
  }
  return nth_root(total, order);
}

