}

void test_neville() {
  List<double> xs = [1.0, 2.0, 3.0, 4.0, 6.0];
  List<double> ys = [6.0, 7.0, 8.0, 9.0, 11.0];
  NevilleResult r1 = neville_interpolate(xs, ys, 5.0);
  if (r1.value != 10.0) {
    throw Exception("neville_interpolate at 5 failed");
