class Point {
  double x;
  double y;
  double z;
  Point({required this.x, required this.y, required this.z});
}

double absf(double x) {
  if (x < 0.0) {
    return -x;
  }
  return x;
}

double sqrt_approx(double x) {
  if (x <= 0.0) {
    return 0.0;
  }
  double guess = x / 2.0;
  int i = 0;
  while (i < 20) {
    guess = (guess + x / guess) / 2.0;
    i = i + 1;
  }
  return guess;
}

double distance(Point a, Point b) {
  double dx = b.x - a.x;
  double dy = b.y - a.y;
  double dz = b.z - a.z;
  return sqrt_approx(absf(dx * dx + dy * dy + dz * dz));
}

String point_to_string(Point p) {
  return "Point(" + (p.x).toString() + ", " + (p.y).toString() + ", " + (p.z).toString() + ")";
}

