bool is_geometric_series(List<double> series) {
  if (series.length == 0) {
    throw Exception("Input list must be a non empty list");
  }
  if (series.length == 1) {
    return true;
  }
  if (series[0] == 0.0) {
    return false;
  }
  double ratio = series[1] / series[0];
  int i = 0;
  while (i < series.length - 1) {
    if (series[i] == 0.0) {
    return false;
  }
    if (series[i + 1] / series[i] != ratio) {
    return false;
  }
    i = i + 1;
  }
  return true;
}

double geometric_mean(List<double> series) {
  if (series.length == 0) {
    throw Exception("Input list must be a non empty list");
  }
  double product = 1.0;
  int i = 0;
  while (i < series.length) {
    product = product * series[i];
    i = i + 1;
  }
  int n = series.length;
  return nth_root(product, n);
}

double pow_float(double base, int exp) {
  double result = 1.0;
  int i = 0;
  while (i < exp) {
    result = result * base;
    i = i + 1;
  }
  return result;
}

double nth_root(double value, int n) {
  if (value == 0.0) {
    return 0.0;
  }
  double low = 0.0;
  double high = value;
  if (value < 1.0) {
    high = 1.0;
  }
  double mid = (low + high) / 2.0;
  int i = 0;
  while (i < 40) {
    double mp = pow_float(mid, n);
    if (mp > value) {
    high = mid;
  } else {
    low = mid;
  }
    mid = (low + high) / 2.0;
    i = i + 1;
  }
  return mid;
}

