int sum_of_digits(int n) {
  int m = abs_int(n);
  int res = 0;
  while (m > 0) {
    res = res + m % 10;
    m = m ~/ 10;
  }
  return res;
}

int sum_of_digits_recursion(int n) {
  int m = abs_int(n);
  if (m < 10) {
    return m;
  }
  return m % 10 + sum_of_digits_recursion(m ~/ 10);
}

