String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

List<double> cramers_rule_2x2(List<double> eq1, List<double> eq2) {
  if (eq1.length != 3 || eq2.length != 3) {
    throw Exception("Please enter a valid equation.");
  }
  if (eq1[0] == 0.0 && eq1[1] == 0.0 && eq2[0] == 0.0 && eq2[1] == 0.0) {
    throw Exception("Both a & b of two equations can't be zero.");
  }
  double a1 = eq1[0];
  double b1 = eq1[1];
  double c1 = eq1[2];
  double a2 = eq2[0];
  double b2 = eq2[1];
  double c2 = eq2[2];
  double determinant = a1 * b2 - a2 * b1;
  double determinant_x = c1 * b2 - c2 * b1;
  double determinant_y = a1 * c2 - a2 * c1;
  if (determinant == 0.0) {
    if (determinant_x == 0.0 && determinant_y == 0.0) {
    throw Exception("Infinite solutions. (Consistent system)");
  };
    throw Exception("No solution. (Inconsistent system)");
  }
  if (determinant_x == 0.0 && determinant_y == 0.0) {
    return [0.0, 0.0];
  }
  double x = determinant_x / determinant;
  double y = determinant_y / determinant;
  return [x, y];
}

void test_cramers_rule_2x2() {
  List<double> r1 = cramers_rule_2x2([2.0, 3.0, 0.0], [5.0, 1.0, 0.0]);
