int largest_square_area_in_matrix_top_down_with_dp(int rows, int cols, List<List<int>> mat) {
  List<int> largest = [0];
  List<List<int>> dp_array = <List<int>>[];
  int r = 0;
  while (r < rows) {
    List<int> row_list = <int>[];
    int c = 0;
    while (c < cols) {
    row_list = [...row_list, -1];
    c = c + 1;
  }
    dp_array = ([...dp_array, row_list] as List).map((e) => ((e as List).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList() as List<int>)).toList();
    r = r + 1;
