Map<int, String> WEEK_DAY_NAMES = {0: "Sunday", 1: "Monday", 2: "Tuesday", 3: "Wednesday", 4: "Thursday", 5: "Friday", 6: "Saturday"};
String get_week_day(int year, int month, int day) {
  if (year < 100) {
    throw Exception("year should be in YYYY format");
  }
  if (month < 1 || month > 12) {
    throw Exception("month should be between 1 to 12");
  }
  if (day < 1 || day > 31) {
    throw Exception("day should be between 1 to 31");
  }
