// Generated by Mochi transpiler
String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { return v.toInt().toString(); } return v.toString(); }

int seed = 1;
int rand() {
  seed = (seed * 1103515245 + 12345) % 2147483648;
  return seed ~/ 65536;
}

int randint(int a, int b) {
  int r = rand();
  return a + r % (b - a + 1);
}

List<int> fisher_yates_shuffle_int(List<int> data) {
  List<int> res = data;
  int i = 0;
  while (i < res.length) {
    int a = randint(0, res.length - 1);
    int b = randint(0, res.length - 1);
    int temp = res[a];
    while (res.length <= a) { res.add(0); } res[a] = res[b];
    while (res.length <= b) { res.add(0); } res[b] = temp;
    i = i + 1;
  }
  return res;
}

List<String> fisher_yates_shuffle_str(List<String> data) {
  List<String> res = data;
  int i = 0;
  while (i < res.length) {
    int a = randint(0, res.length - 1);
    int b = randint(0, res.length - 1);
    String temp = res[a];
    while (res.length <= a) { res.add(""); } res[a] = res[b];
    while (res.length <= b) { res.add(""); } res[b] = temp;
    i = i + 1;
  }
  return res;
}

List<int> integers = [0, 1, 2, 3, 4, 5, 6, 7];
List<String> strings = ["python", "says", "hello", "!"];
void main() {
  print("Fisher-Yates Shuffle:");
  print("List " + _str(integers) + " " + _str(strings));
  print("FY Shuffle " + _str(fisher_yates_shuffle_int(integers)) + " " + _str(fisher_yates_shuffle_str(strings)));
}
