String _substr(String s, num start, num end) {
  var n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  return s.substring(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { return v.toInt().toString(); } return v.toString(); }

double to_float(int x) {
  return x * 1.0;
}

double ln(double x) {
  if (x <= 0.0) {
    throw Exception("ln domain error");
  }
  double y = (x - 1.0) / (x + 1.0);
  double y2 = y * y;
  double term = y;
  double sum = 0.0;
  int k = 0;
  while (k < 10) {
    double denom = to_float(2 * k + 1);
    sum = sum + term / denom;
    term = term * y2;
    k = k + 1;
  }
  return 2.0 * sum;
}

double exp(double x) {
  double term = 1.0;
  double sum = 1.0;
  int n = 1;
  while (n < 20) {
    term = term * x / to_float(n);
    sum = sum + term;
    n = n + 1;
  }
  return sum;
}

double pow_float(double base, double exponent) {
  return exp(exponent * ln(base));
}

double get_altitude_at_pressure(double pressure) {
  if (pressure > 101325.0) {
    throw Exception("Value Higher than Pressure at Sea Level !");
  }
  if (pressure < 0.0) {
    throw Exception("Atmospheric Pressure can not be negative !");
  }
  double ratio = pressure / 101325.0;
  return 44330.0 * (1.0 - pow_float(ratio, 1.0 / 5.5255));
}

void main() {
  print(_str(get_altitude_at_pressure(100000.0)));
  print(_str(get_altitude_at_pressure(101325.0)));
  print(_str(get_altitude_at_pressure(80000.0)));
}
