// Generated by Mochi transpiler
dynamic _substr(dynamic s, num start, num end) {
  int n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  if (s is String) {
    return s.substring(s0, e0);
  }
  return s.sublist(s0, e0);
}


Never _error(String msg) {
  throw Exception(msg);
}

double G = 9.80665;
double potential_energy(double mass, double height) {
  if (mass < 0.0) {
    _error("The mass of a body cannot be negative");
  }
  if (height < 0.0) {
    _error("The height above the ground cannot be negative");
  }
  return mass * G * height;
}

void main() {
  print(potential_energy(10.0, 10.0));
  print(potential_energy(10.0, 5.0));
  print(potential_energy(2.0, 8.0));
}
