String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { var i = v.toInt(); if (i == 0) return '0'; return i.toString(); } return v.toString(); }

int solution(int n) {
  Map<int, bool> composite = <int, bool>{};
  int total = 0;
  int _num = 2;
  while (_num < n) {
    if (!(composite[_num] ?? false)) {
    total = total + _num;
