// Generated by Mochi transpiler
dynamic _substr(dynamic s, num start, num end) {
  int n = s.length;
  int s0 = start.toInt();
  int e0 = end.toInt();
  if (s0 < 0) s0 += n;
  if (e0 < 0) e0 += n;
  if (s0 < 0) s0 = 0;
  if (s0 > n) s0 = n;
  if (e0 < 0) e0 = 0;
  if (e0 > n) e0 = n;
  if (s0 > e0) s0 = e0;
  if (s is String) {
    return s.substring(s0, e0);
  }
  return s.sublist(s0, e0);
}

String _str(dynamic v) { if (v is double && v == v.roundToDouble()) { var i = v.toInt(); if (i == 0) return '0'; return i.toString(); } return v.toString(); }

List<int> concat(List<int> a, List<int> b) {
  List<int> result = <int>[];
  for (int x in a) {
    result = [...result, x];
  }
  for (int x in b) {
    result = [...result, x];
  }
  return result;
}

List<int> quick_sort(List<int> data) {
  if (data.length <= 1) {
    return data;
  }
  int pivot = data[0];
  List<int> left = <int>[];
  List<int> right = <int>[];
  int i = 1;
  while (i < data.length) {
    int e = data[i];
    if (e <= pivot) {
    left = [...left, e];
  } else {
    right = [...right, e];
  }
    i = i + 1;
  }
  List<int> sorted_left = quick_sort(left);
  List<int> sorted_right = quick_sort(right);
  List<int> left_pivot = [...sorted_left, pivot];
  return ([...left_pivot, ...sorted_right] as List<dynamic>).map((e) => (e is BigInt ? e.toInt() : (e as int))).toList();
}

void main() {
  print(_str(quick_sort([2, 1, 0])));
  print(_str(quick_sort([3, 5, 2, 4, 1])));
}
