}

int mismatch_in_text(String text, String pat, int current_pos) {
  int i = pat.length - 1;
  while (i >= 0) {
    if (_substr(pat, i, i + 1) != _substr(text, current_pos + i, current_pos + i + 1)) {
    return current_pos + i;
  }
    i = i - 1;
  }
  return -1;
}

