String normalize(String input_str) {
  String res = "";
  int i = 0;
  while (i < input_str.length) {
    String ch = _substr(input_str, i, i + 1);
    String lc = char_to_lower(ch);
    if (lc.compareTo("a") >= 0 && lc.compareTo("z") <= 0) {
    res = res + lc;
  }
    i = i + 1;
  }
  return res;
}

