assert_int(Name, Actual, Expected) ->
    try
        case (Actual == Expected) of
        true -> io:format("~ts~n", [mochi_repr((Name ++ " ok"))]);
        _ -> io:format("~ts~n", [mochi_repr(((((Name ++ " fail ") ++ int_to_string(Actual)) ++ " != ") ++ int_to_string(Expected)))])
    end,
        nil
    catch {return, Ret} -> Ret end.

