        case (differentiate(F1, 2.0, 2) /= 2.0) of
        true -> erlang:error("f1 failed");
        _ -> ok
    end,
        F2 = fun F2(X_3) ->
    try
        dual_mul(dual_pow(X_3, 2), dual_pow(X_3, 4))
    catch {return, Ret} -> Ret end
end,
        case (differentiate(F2, 9.0, 2) /= 196830.0) of
        true -> erlang:error("f2 failed");
        _ -> ok
    end,
        F3 = fun F3(Y) ->
    try
