        case (differentiate(F3, 3.5, 4) /= 7605.0) of
        true -> erlang:error("f3 failed");
        _ -> ok
    end,
        F4 = fun F4(Y_2) ->
    try
        dual_pow(Y_2, 2)
    catch {return, Ret} -> Ret end
end,
        case (differentiate(F4, 4.0, 3) /= 0.0) of
        true -> erlang:error("f4 failed");
        _ -> ok
    end,
        nil
    catch {return, Ret} -> Ret end.

main() ->
    try
        test_differentiate(),
        F = fun F(Y_3) ->
