{I_9, J_10, N, Result_9, Row_12} = Fun_10(I_9, J_9, N, Result_9, Row_9),
            Result_10 = lists:append((case Result_9 of nil -> []; _ -> Result_9 end), [Row_12]),
            I_10 = (I_9 + 1),
