using System;
using System.Collections.Generic;
using System.Linq;
struct People {
    public string name;
    public int age;
    public override string ToString() => $"{{name: {name}, age: {age}}}";
}
struct PersonResult {
    public string name;
    public int age;
    public bool is_senior;
    public override string ToString() => $"{{name: {name}, age: {age}, is_senior: {is_senior}}}";
}
class Program {
    static People[] people = new People[]{new People{name = "Alice", age = 30}, new People{name = "Bob", age = 15}, new People{name = "Charlie", age = 65}, new People{name = "Diana", age = 45}};
    static PersonResult[] adults = (from person in people where (person.age >= 18) select new PersonResult{name = person.name, age = person.age, is_senior = (person.age >= 60)}).ToArray();
    static void Main() {
        Console.WriteLine("--- Adults ---");
        foreach (var person in adults) {
            Console.WriteLine(string.Join(" ", new object[]{person.name, "is", person.age, (person.is_senior ? " (senior)" : """")}).TrimEnd());
        }
    }
}
