using System;
struct Person {
    public string name;
    public int age;
    public string status;
    public override string ToString() => $"Person {{name = {name}, age = {age}, status = {status}}}";
}
class Program {
    static Person[] people = new Person[]{new Person{name = "Alice", age = 17, status = "minor"}, new Person{name = "Bob", age = 25, status = "unknown"}, new Person{name = "Charlie", age = 18, status = "unknown"}, new Person{name = "Diana", age = 16, status = "minor"}};
    static void Main() {
        for (int i = 0; i < people.Length; i++) {
            var item = people[i];
            if ((age >= 18)) {
                item.status = "adult";
                item.age = (age + 1);
            }
            people[i] = item;
        }
        Console.WriteLine("ok");
    }
}
