void prettyPrint(String dna, int rowLen) {
  print('SEQUENCE:');
  var le = dna.length;
  var i = 0;
  while (i.toString().compareTo(le.toString()) < 0) {
    var k = (i as num) + rowLen;
    if (k.toString().compareTo(le.toString()) > 0) {
      k = le;
    }
    print(padLeft(i.toString(), 5) + ': ' + dna.substring(i, k));
    i = (i as num) + rowLen;
  }
  var a = 0;
  var c = 0;
  var g = 0;
  var t = 0;
  var idx = 0;
  while (idx.toString().compareTo(le.toString()) < 0) {
    var ch = dna.toString().substring(idx, (idx as num) + 1);
    if (ch == 'A') {
      a = (a as num) + 1;
    }
    else {
      if (ch == 'C') {
        c = (c as num) + 1;
      }
      else {
        if (ch == 'G') {
          g = (g as num) + 1;
        }
        else {
          if (ch == 'T') {
            t = (t as num) + 1;
          }
        }
      }
    }
    idx = (idx as num) + 1;
  }
  print('');
  print('BASE COUNT:');
  print('    A: ' + padLeft(a.toString(), 3));
  print('    C: ' + padLeft(c.toString(), 3));
  print('    G: ' + padLeft(g.toString(), 3));
  print('    T: ' + padLeft(t.toString(), 3));
  print('    ------');
  print('    Σ: ' + le.toString());
  print('    ======');
}

