function fib(n: number): number {
  if ((n < 2)) {
    return n;
  }
  var a = 0;
  var b = 1;
  var i = 1;
  while ((i < n)) {
    let t = a + b;
    a = b;
    b = t;
    i = i + 1;
  }
  return b;
}

