// Generated by Mochi transpiler
bool isPrime(int n) {
  if (n < 2) {
    return false;
  }
  if (n % 2 == 0) {
    return n == 2;
  }
  if (n % 3 == 0) {
    return n == 3;
  }
  int d = 5;
  while (d * d <= n) {
    if (n % d == 0) {
    return false;
  }
    d = d + 2;
    if (n % d == 0) {
    return false;
  }
    d = d + 4;
  }
  return true;
}

List<int> asc = [];
void gen(int first, int cand, int digits) {
  if (digits == 0) {
    if (isPrime(cand)) {
    asc = asc + [cand];
  };
    return;
  }
  int i = first;
  while (i < 10) {
    gen(i + 1, cand * 10 + i, digits - 1);
    i = i + 1;
  }
}

String pad(int n, int width) {
  String s = (n).toString();
  while (s.length < width) {
    s = " " + s;
  }
  return s;
}

void main() {
  int digits = 1;
  while (digits < 10) {
    gen(1, 0, digits);
    digits = digits + 1;
  }
  print("There are " + (asc.length).toString() + " ascending primes, namely:");
  int i = 0;
  String line = "";
  while (i < asc.length) {
    line = line + pad(asc[i], 8) + " ";
    if ((i + 1) % 10 == 0) {
    print(line.substring(0, line.length - 1));
    line = "";
  }
    i = i + 1;
  }
  if (line.length > 0) {
    print(line.substring(0, line.length - 1));
  }
}

void _start() {
  main();
