// Generated by Mochi transpiler
List<List<BigInt>> bellTriangle(int n) {
  List<List<BigInt>> tri = [];
  int i = 0;
  while (i < n) {
    List<BigInt> row = [];
    int j = 0;
    while (j < i) {
    row = [...row, BigInt.from(0)];
    j = j + 1;
  }
    tri = [...tri, row];
    i = i + 1;
  }
  tri[1]![0] = BigInt.from(1);
  i = 2;
  while (i < n) {
    tri[i]![0] = tri[i - 1]![i - 2];
    int j = 1;
    while (j < i) {
    tri[i]![j] = tri[i]![j - 1] + tri[i - 1]![j - 1];
    j = j + 1;
  }
    i = i + 1;
  }
  return tri;
}

void main() {
  final List<List<BigInt>> bt = bellTriangle(51);
  print("First fifteen and fiftieth Bell numbers:");
  for (int i = 1; i < 16; i++) {
    print("" + (i).toString().padLeft(2, " ") + ": " + (bt[i]![0]).toString());
  }
  print("50: " + (bt[50]![0]).toString());
  print("");
  print("The first ten rows of Bell's triangle:");
  for (int i = 1; i < 11; i++) {
    print(bt[i]);
  }
}

void _start() {
  main();
}
