function bellTriangle(n: number): number[][] {
  let tri: number[][] = [];
  let i: number = 0;
  while ((i < n)) {
    let row: number[] = [];
    let j: number = 0;
    while ((j < i)) {
      row = [...row, 0];
      j = (j + 1);
    }
    tri = [...tri, row];
    i = (i + 1);
  }
  tri[1][0] = 1;
  i = 2;
  while ((i < n)) {
    tri[i][0] = tri[(i - 1)][(i - 2)];
    let j: number = 1;
    while ((j < i)) {
      tri[i][j] = (tri[i][(j - 1)] + tri[(i - 1)][(j - 1)]);
      j = (j + 1);
    }
    i = (i + 1);
  }
  return tri;
}
