    case true of
        true ->
            try
                Ip_2 = computeIP(N_11, Primes_4),
                Pw_2 = ((6 * N_11) + 3),
                case (Pw_2 > DenomPow) of
        true -> Sum_2 = bigMulPow10(Sum, (Pw_2 - DenomPow)),
            Prev_2 = bigMulPow10(Prev, (Pw_2 - DenomPow)),
            DenomPow_2 = Pw_2,
            DenomPow_3 = DenomPow_2,
            Prev_3 = Prev_2,
            Sum_3 = Sum_2;
        _ -> DenomPow_3 = DenomPow,
            Prev_3 = Prev,
            Sum_3 = Sum
